4'),(6014,434,'_steps','field_60f70910881ca'),(6015,435,'_wp_attached_file','placeholder-single-image.jpg'),(6016,435,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:1352;s:6:\"height\";i:800;s:4:\"file\";s:28:\"placeholder-single-image.jpg\";s:5:\"sizes\";a:10:{s:6:\"medium\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-300x178.jpg\";s:5:\"width\";i:300;s:6:\"height\";i:178;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:5:\"large\";a:4:{s:4:\"file\";s:37:\"placeholder-single-image-1024x606.jpg\";s:5:\"width\";i:1024;s:6:\"height\";i:606;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:9:\"thumbnail\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-150x150.jpg\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:12:\"medium_large\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-768x454.jpg\";s:5:\"width\";i:768;s:6:\"height\";i:454;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:2:\"xs\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-320x189.jpg\";s:5:\"width\";i:320;s:6:\"height\";i:189;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:2:\"sm\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-768x454.jpg\";s:5:\"width\";i:768;s:6:\"height\";i:454;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:2:\"md\";a:4:{s:4:\"file\";s:37:\"placeholder-single-image-1024x606.jpg\";s:5:\"width\";i:1024;s:6:\"height\";i:606;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:9:\"xs-square\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-320x320.jpg\";s:5:\"width\";i:320;s:6:\"height\";i:320;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:9:\"sm-square\";a:4:{s:4:\"file\";s:36:\"placeholder-single-image-768x768.jpg\";s:5:\"width\";i:768;s:6:\"height\";i:768;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:9:\"md-square\";a:4:{s:4:\"file\";s:37:\"placeholder-single-image-1024x800.jpg\";s:5:\"width\";i:1024;s:6:\"height\";i:800;s:9:\"mime-type\";s:10:\"image/jpeg\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"c